import java.util.Calendar;public class P5_3 {    static class InvalidDateFormatException extends Exception {        public InvalidDateFormatException(String msg) {            super(msg);        }    }    static class DateParser {        public Calendar parse(String dateStr) throws InvalidDateFormatException {            if (!dateStr.matches("\\d{4}-\\d{2}-\\d{2}")) {                throw new InvalidDateFormatException("형식 오류");            }            String[] parts = dateStr.split("-");            int year = Integer.parseInt(parts[0]);            int month = Integer.parseInt(parts[1]) - 1;             int day = Integer.parseInt(parts[2]);            Calendar cal = Calendar.getInstance();            cal.setLenient(false);            try {                cal.set(year, month, day);                cal.getTime();             } catch (Exception e) {                throw new InvalidDateFormatException("존재하지 않는 날짜");            }            return cal;        }    }    public static void main(String[] args) {        String[] tests = {                "2025-05-17",                "2025/05/17",                "hello-world",                "2024-02-30"        };        DateParser parser = new DateParser();        for (String t : tests) {            System.out.println("입력: " + t);            try {                Calendar cal = parser.parse(t);                int dayOfWeek = cal.get(Calendar.DAY_OF_WEEK);                String day;                switch (dayOfWeek) {                    case Calendar.MONDAY: day = "월요일"; break;                    case Calendar.TUESDAY: day = "화요일"; break;                    case Calendar.WEDNESDAY: day = "수요일"; break;                    case Calendar.THURSDAY: day = "목요일"; break;                    case Calendar.FRIDAY: day = "금요일"; break;                    case Calendar.SATURDAY: day = "토요일"; break;                    case Calendar.SUNDAY: day = "일요일"; break;                    default: day = "알 수 없음";                }                System.out.println("요일: " + day);            } catch (InvalidDateFormatException e) {                System.out.println("잘못된 날짜 형식입니다.");            }            System.out.println();        }    }}